import { Component, PipeTransform } from '@angular/core';
import { Company } from 'src/app/models/icompany';
import { CompanyService } from 'src/app/services/company.service';
import Swal from 'sweetalert2';
import { NgbPaginationModule, NgbTypeaheadModule } from '@ng-bootstrap/ng-bootstrap';
import { NgbHighlight } from '@ng-bootstrap/ng-bootstrap';
import { DecimalPipe } from '@angular/common';
import { Observable, map, startWith } from 'rxjs';
import { FormControl } from '@angular/forms';

let list: Company[] = [];

function search(text: string, pipe: PipeTransform): Company[] {
	return list.filter((x) => {
		const term = text.toLowerCase();
		return (
			x.name.toLowerCase().includes(term) ||
			pipe.transform(x.nit).includes(term) 
		);
	});
}

@Component({
  selector: 'app-shared',
  templateUrl: './shared.component.html',
  styleUrls: ['./shared.component.scss']
})

export class SharedComponent {

  count: Observable<Company[]> | undefined;
  filter=new FormControl('', { nonNullable: true });

  constructor(private service: CompanyService, private pipe: DecimalPipe) {

  }

  ngOnInit(){
    this.getCompany();
    this.count = this.filter.valueChanges.pipe(
      startWith(''),
			map((text) => search(text, this.pipe)),
    )
  }


  getCompany(){

    this.service.getCompany().subscribe(
      (r) =>{
        list = r.data;
      },
      (error)=>{
        Swal.fire({
          icon: "error",
          title: "Oops...",
          text: error
        });
      }
    )
  }



}
